import {
  Component,
  ChangeDetectionStrategy,
  inject,
  OnInit,
} from '@angular/core';
import { NotificationService } from '../../services/notification-service';
import { Notifications } from '../../models/notification.model';
import { CommonModule } from '@angular/common';
import { CommonService } from '../../services/commonService/common-service';
import { timer, Observable, of } from 'rxjs';
import { switchMap, map, shareReplay } from 'rxjs/operators';
import { materialImports } from '../../models/material.imports';

@Component({
  selector: 'app-notification',
  standalone: true,
  imports: [CommonModule, materialImports],
  templateUrl: './notification.html',
  styleUrl: './notification.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class Notification implements OnInit {
  private notificationService = inject(NotificationService);
  private commonService = inject(CommonService);
  private currentUser = this.commonService.getCurrentUser();

  public notifications$: Observable<Notifications[]> = of([]);
  public unreadCount$: Observable<number> = of(0);

  ngOnInit(): void {
    if (!this.currentUser?.id) {
      this.notifications$ = timer(0, 1000 * 30).pipe(map(() => []));
      this.unreadCount$ = this.notifications$.pipe(map(() => 0));
      return;
    }

    this.notifications$ = timer(0, 1000 * 30).pipe(
      switchMap(() =>
        this.notificationService.getNotifications(this.currentUser.id)
      ),
      shareReplay(1)
    );

    this.unreadCount$ = this.notifications$.pipe(
      map((notifs) => notifs.filter((n) => !n.read).length)
    );
  }

  public openNotification(n: Notifications): void {
    if (!n.read && n.id) {
      this.notificationService.markAsRead(n.id).subscribe();
    }
  }
}

import { Component, Inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  FormBuilder,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { provideNativeDateAdapter } from '@angular/material/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { ProjectService } from '../project-service';
import { CommonService } from '../../core/services/commonService/common-service';
import { NotificationService } from '../../core/services/notification-service';
import { materialImports } from '../../core/models/material.imports';
import { Projects } from '../../core/models/project.model';

@Component({
  selector: 'app-project-form',
  imports: [CommonModule, ReactiveFormsModule, materialImports],
  providers: [provideNativeDateAdapter()],
  templateUrl: './project-form.html',
  styleUrl: './project-form.scss',
})
export class ProjectForm {
  public form: FormGroup;
  public currentDate: Date = new Date();
  public projectId: number = 0;
  public users: { id: string; fullName: string }[] = [{ id: '', fullName: '' }];
  public statuses = ['To Do', 'In Progress', 'Done'];
  public projectData = {} as Projects;

  constructor(
    private fb: FormBuilder,
    @Inject(MAT_DIALOG_DATA) public data: any,
    public dialogRef: MatDialogRef<ProjectForm>,
    private projectService: ProjectService,
    private commonService: CommonService,
    private notificationService: NotificationService
  ) {
    if (this.data && this.data.id) {
      this.projectId = this.data.id;
      this.getProject();
    }
    this.getUsers();
    this.form = this.fb.group({
      name: [this.projectData.name || '', Validators.required],
      startDate: [
        this.projectData.startDate || this.currentDate,
        Validators.required,
      ],
      deadlineDate: [this.projectData.deadlineDate || ''],
      assignedTo: [this.projectData.assignedTo || '', Validators.required],
      status: [this.projectData.status || '', Validators.required],
      description: [this.projectData.description || '', [Validators.required]],
    });
  }

  private getProject() {
    this.projectService.getProject(this.projectId).subscribe(
      (response) => {
        this.projectData = response;
        const { id, ...pro } = response;
        this.form.setValue(pro);
      },
      (error) => {
        console.error(error);
      }
    );
  }

  private getUsers() {
    this.commonService.fetchUsers().subscribe((res) => {
      this.users = res.map(({ id, fullName }) => ({ id, fullName }));
    });
  }

  public onSubmit(): void {
    if (this.form.valid) {
      const UpdatedProject = { ...this.form.value, id: this.projectData.id };

      const response = this.projectId
        ? this.projectService.updateProject(UpdatedProject, this.projectId)
        : this.projectService.addProject(UpdatedProject);

      response.subscribe(
        (result) => {
          if (result) {
            this.dialogRef.close(true);
            const message = this.projectId
              ? 'Project Updated'
              : 'Project Created';
            this.commonService.openSuccessSnackBar(message, 'ok');
            if (result.assignedTo !== this.projectData.assignedTo) {
              this.notifyUser(result);
            }
          }
        },
        (error) => {
          this.commonService.openFailureSnackBar(
            'something went wrong',
            'try again!'
          );
        }
      );
    }
  }

  private notifyUser(project: Projects) {
    this.notificationService
      .create({
        userId: project.assignedTo,
        type: 'new-assignment',
        message: `Youve been assigned to Project ${project.name}`,
        projectId: project.id,
        read: false,
        createdAt: new Date().toISOString(),
      })
      .subscribe(
        () => {},
        (err) => {
          this.commonService.openFailureSnackBar(
            'something went wrong',
            'try again!'
          );
        }
      );
  }
}

import {
  Component,
  ViewChild,
  OnInit,
  OnDestroy,
  ChangeDetectorRef,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatTableDataSource } from '@angular/material/table';
import { MatDialog } from '@angular/material/dialog';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { MatPaginator } from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';
import { Subject, takeUntil } from 'rxjs';
import { ProjectForm } from '../project-form/project-form';
import { Header } from '../../shared/components/header/header';
import { CommonService } from '../../core/services/commonService/common-service';
import { DuedatePipe } from '../../shared/pipes/duedate-pipe';
import { ProjectService } from '../project-service';
import { Router } from '@angular/router';
import { UserNamePipe } from '../../shared/pipes/user-name-pipe';
import { materialImports } from '../../core/models/material.imports';
import { Projects } from '../../core/models/project.model';
import { Users } from '../../core/models/user.model';

@Component({
  selector: 'app-project-list',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    ProjectForm,
    Header,
    DuedatePipe,
    materialImports,
    UserNamePipe,
  ],
  templateUrl: './project-list.html',
  styleUrls: ['./project-list.scss'],
})
export class ProjectListComponent implements OnInit, OnDestroy {
  public displayedColumns: string[] = [
    'id',
    'name',
    'assignTo',
    'status',
    'startDate',
    'deadline',
    'description',
    'actions',
  ];

  public dataSource = new MatTableDataSource<Projects>([]);
  public isLoading = false;

  private destroy$ = new Subject<void>();
  private projects: Projects[] = [];
  public users: Users[] = [];

  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort;

  constructor(
    private dialog: MatDialog,
    private commonService: CommonService,
    private projectService: ProjectService,
    private router: Router,
    private cdr: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.commonService.search$
      .pipe(takeUntil(this.destroy$))
      .subscribe((term) => {
        const filtered = this.commonService.filteredSearchData(
          term,
          this.projects,
          ['name', 'status']
        );
        this.dataSource.data = filtered;
        this.cdr.detectChanges();
      });

    this.getUsers();
  }

  ngAfterViewInit(): void {
    this.fetchProjects();
  }

  private getUsers() {
    this.commonService.fetchUsers().subscribe((next) => {
      this.users = next;
    });
  }

  private updateDatasource() {
    this.dataSource.data = this.projects;
    this.dataSource.paginator = this.paginator;
    this.dataSource.sort = this.sort;

    // Default sort: ID ascending
    this.sort.active = 'id';
    this.sort.direction = 'asc';

    this.sort.sortChange.emit({
      active: this.sort.active,
      direction: this.sort.direction,
    });
  }

  private fetchProjects(): void {
    this.projectService.fetchProjects().subscribe({
      next: (projects) => {
        this.projects = projects;
        this.updateDatasource();
      },
      error: (err) => {
        console.error('Failed to fetch projects:', err);
        this.isLoading = false;
      },
    });
  }

  public openForm(id?: number): void {
    const dialogRef = this.dialog.open(ProjectForm, {
      data: { id },
      width: '400px',
    });

    dialogRef.afterClosed().subscribe((result) => {
      if (result) this.fetchProjects();
    });
  }

  public onDelete(projectId: number, projectName: string): void {
    this.projectService.deleteProject(projectId).subscribe({
      next: (res) => {
        if (res) {
          this.fetchProjects();
          this.commonService.openSuccessSnackBar(
            `${projectName} Project Deleted`
          );
        }
      },
      error: () => alert('Something went wrong'),
    });
  }

  public addNew(): void {
    this.dialog.open(ProjectForm, { width: '400px' });
  }

  public redirectToTask(id: string): void {
    this.router.navigate(['/projects', id, 'tasks']);
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

